package JavaBasics;

public class DataTypes {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//primitive data types:int, double, char & boolean
		
		//int
		int i=10;
//		i=20;  //replacing 10 by 20
		
		//double
		double a=10.11;
		double b=10;
		
		//char is single digit val & should be written within single quotes!
		char c='M';
		char d='*';
		char e='1';
		
		//boolean can be true/false
		boolean b1=true;
		boolean b2=false;		
		
		//String is a class not a datatype
		String s1="Its mine";
		
        System.out.println(i);
		System.out.println(b1);
		System.out.println(s1);
}

}
